mapper.select:
SELECT COUNT(*) FROM Asset_Disposal INNER JOIN Asset ON Asset_Disposal.asset_id = Asset.id INNER JOIN Category ON Asset.category_id = Category.id
Asset.category_id = #{categoryId}
Asset.asset_name LIKE '%' || #{keyword} || '%'

WITH FindLastStatus AS ( SELECT asset_id, user_id, create_date, status, ROW_NUMBER() OVER(PARTITION BY asset_id, user_id ORDER BY create_date DESC, id DESC) as rn FROM Asset_History WHERE user_id = #{userId} ), InUseAssets AS ( SELECT asset_id, create_date AS acquisition_date FROM FindLastStatus WHERE rn = 1 AND status = 'rent' ), CurrentRent AS ( SELECT rc.asset_id, r.return_date, ROW_NUMBER() OVER(PARTITION BY rc.asset_id ORDER BY r.rent_date DESC, r.id DESC) as rn FROM Rent_Content rc JOIN Rent r ON rc.rent_id = r.id ) SELECT COUNT(*) FROM Asset a INNER JOIN Category c ON a.category_id = c.id INNER JOIN InUseAssets iua ON a.id = iua.asset_id LEFT JOIN CurrentRent cr ON iua.asset_id = cr.asset_id AND cr.rn = 1
a.user_id = #{userId}
AND a.category_id = #{categoryId}
AND a.asset_name LIKE '%' || #{keyword} || '%'
ORDER BY a.id DESC

WITH FindLastStatus AS ( SELECT asset_id, user_id, create_date, status, ROW_NUMBER() OVER(PARTITION BY asset_id, user_id ORDER BY create_date DESC, id DESC) as rn FROM Asset_History WHERE user_id = #{userId} ), InUseAssets AS ( SELECT asset_id, create_date AS acquisition_date FROM FindLastStatus WHERE rn = 1 AND status = 'rent' ), CurrentRent AS ( SELECT rc.asset_id, r.return_date, ROW_NUMBER() OVER(PARTITION BY rc.asset_id ORDER BY r.rent_date DESC, r.id DESC) as rn FROM Rent_Content rc JOIN Rent r ON rc.rent_id = r.id ) SELECT a.id AS asset_id, a.asset_name, c.category_name, a.serial_number, iua.acquisition_date AS create_date, cr.return_date FROM Asset a INNER JOIN Category c ON a.category_id = c.id INNER JOIN InUseAssets iua ON a.id = iua.asset_id LEFT JOIN CurrentRent cr ON iua.asset_id = cr.asset_id AND cr.rn = 1
a.user_id = #{userId}
AND a.category_id = #{categoryId}
AND a.asset_name LIKE '%' || #{keyword} || '%'
ORDER BY a.id DESC OFFSET #{offset} ROWS FETCH NEXT #{size} ROWS ONLY

mapper.update:
UPDATE Asset
SET 
    user_id = #{userId}
    ,location = #{location}
WHERE serial_number = #{serialNumber}
	AND id = #{assetId}

mapper.select:
SELECT COUNT(A.user_id)
FROM Asset A
WHERE A.id = #{assetId}

mapper.update:
UPDATE Asset
SET is_request = 1
WHERE id = #{assetId}

mapper.select:
    	SELECT COUNT(id)
		FROM Asset_Return
		WHERE user_id = #{userId}
		AND asset_id = #{assetId}

    	SELECT is_return AS returning
		FROM (
		    SELECT is_return
		    FROM Asset_Return
		    WHERE user_id = #{userId}
		      AND asset_id = #{assetId}
		    ORDER BY request_date DESC
		)
		WHERE ROWNUM = 1
